import React from 'react'
import {Link} from 'react-router-dom'
import './AddAuthor.css'

class AddAuthor extends React.Component{
    render(){
        return <div>
            <h1>Add Author</h1>
            <AuthorForm onAddAuthor={this.props.onAddAuthor}></AuthorForm>
            <Link to="/">Go to Home Page</Link>
        </div>;
    }
}

class AuthorForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name : '',
            imageUrl : ''
        };

        this.onFieldChange = this.onFieldChange.bind(this);

    }

    onFieldChange(event){
        this.setState({
            [event.target.name] : event.target.value
        });
    }

    render(){
        return <div className="AddAuthor">
            <form>
                <label htmlFor="name">Name:</label><br></br>
                <input type="text" name="name" value={this.state.name} onChange={this.onFieldChange}></input><br></br>
                <label htmlFor="imageUrl">Image URL:</label><br></br>
                <input type="text" name="imageUrl" value={this.state.imageUrl} onChange={this.onFieldChange}></input><br></br>
                </form>
            </div>
    }
}

export default AddAuthor;
