import React from 'react'
import {Link} from 'react-router-dom'
import './AddAuthor.css'

class AddAuthor extends React.Component{
    render(){
        return <div>
            <h1>Add Author</h1>
            <AuthorForm></AuthorForm>
            <Link to="/">Go to Home Page</Link>
        </div>;
    }
}

class AuthorForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name : '',
            imageUrl : ''
        }

    }
    render(){
        return <div className="AddAuthor">
                <label htmlFor="name">Name:</label><br></br>
                <input type="text" name="name" value={this.state.name}></input><br></br>
                <label htmlFor="imageUrl">Image URL:</label><br></br>
                <input type="text" name="imageUrl" value={this.state.imageUrl}></input><br></br>
            </div>
    }
}

export default AddAuthor;
